<template>	<view class="wrap">		<view class="tabs_wrap">			<view :class="{'active':i===active,'tab_item':true}" @click="tabChange(i)" v-for="(v,i) in tabs" :key="i">				{{v}}</view>		</view>		<view class="list_wrap">			<view class="list_item" v-for="(v,i) in list" :key="i">				<image v-if="i%2==0" src="@/static/yq@2x.png"></image>				<image v-else src="@/static/tl@2x.png"></image>				<view>					<view class="content">{{v.content}}</view>					<view class="time">{{v.time}}</view>				</view>			</view>		</view>	</view></template><script>	export default {		data() {			return {				active: 0,				tabs: ['全部消息', '邀请通知', '讨论区'],				title: 'Hello',				list: [{					url: '@/static/yq@2x.png',					time: '2020-06-30 10:36',					content: '应急指挥中心邀请您加入应急事件'				}, {					url: '@/static/tl@2x.png',					time: '2020-06-30 10:36',					content: '应急指挥中心邀请您加入应急事件'				}]			}		},		onLoad() {		},		methods: {			tabChange(i) {				this.active = i				this.list = this.list.concat(this.list)			}		}	}</script><style lang="scss">	.wrap {		display: flex;		flex-direction: column;		align-items: center;		justify-content: center;	}	.tabs_wrap {		height: 50px;		border-bottom: 1px solid #E4E9EE;		color: #4E5E72;		font-size: 20px;		display: flex;		align-items: center;		width: 100%;	}	.tab_item {		padding: 0 16px;	}	.active {		font-weight: 600;		color: #1D2732;	}		.list_wrap{		display: flex;		flex-direction: column;		width: 100%;		padding: 0  10px 10px;		box-sizing: border-box;	}	.list_item{		border-bottom: 1px solid #E4E9EE;		padding: 10px 0;		display: flex;		align-items: center;	}	.list_item image{		width: 50px;		height: 50px;		flex-shrink: 0;		margin-right: 10px;	}	.content{		font-size: 15px;		font-weight: 400;		color: #1D2732;	}	.time{		margin-top: 10px;		font-size: 13px;		font-weight: 400;		color: #9EAEC1;	}</style>